mapper.select:
select c1.*,c2.college_name,c2.sf from college_zsjh c1,college c2,college_rank cr
where c1.college_id =c2.college_id and cr.college_id=c2.college_id and cr.type=#{type}
order by cr.rank

select  c1.*,c2.college_name,c2.sf from college_zsjh c1,college c2,college_rank cr
where c1.college_id =c2.college_id and cr.college_id=c2.college_id

    and    c2.college_name like #{0}


and cr.type=#{1}
order by cr.rank

select c1.*,c2.college_name,c2.sf from college_zsjh c1,college c2,college_rank cr where c1.college_id =c2.college_id and cr.college_id=c2.college_id and c1.zs_id in ( SELECT zs_id from college_choose_subject where zs_id NOT in ( SELECT zs_id from college_choose_subject where xkkm not in (#{xkkm1},#{xkkm2},#{xkkm3},#{bxkm}) ) )
and c2.college_name like #{collegeName}
and cr.type=#{type} order by cr.rank
